(115, 1, '2019-08-28 21:57:09', '2019-08-29 01:57:09', '', 'Posts', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2019-08-28 21:57:09', '2019-08-29 01:57:09', '', 114, 'http://localhost:8888/TheHotPickle/2019/08/28/114-revision-v1/', 0, 'revision', '', 0);
